INSERT INTO `comments` (`id`, `page_id`, `user_id`, `created_at`, `clarte`, `interet`, `comment`, `updated_at`) VALUES
(1, 223, 260, 1476868985, 3, 3, 'J\'aurai juste une remarque par rapport à cette page, CeltX dans sa dernière version n\'est plus gratuit. Et l\'abonnement pour synchroniser les documents sur n\'importe quel appareil (tablette, ordinateur, smartphone) amène sur un site qui n\'a pas de traduction française pour les termes techniques.', NULL),
(2, 9, 260, 1479037623, 5, 5, NULL, NULL),
(3, 554, 260, 1487173373, 4, 5, '\"l’auteur a surtout envie d’être aimé. Il a surtout envie qu’on aime ce qu’il produit.\"\r\n\r\nNormalement, on ne devrait pas avoir envie d\'être aimé. Si le lecteur aime l\'histoire, peu importe qu\'il aime son auteur, c\'est ce que je disais souvent dans les forums photo où on soumettait sa photo à la critique :\r\n\"Ne confondez pas aimer une œuvre et aimer son auteur\"\r\n\r\n;-)', NULL),
(4, 237, 260, 1487350968, 5, 5, NULL, NULL),
(5, 555, 260, 1487505911, 5, 5, 'Excellente précision sur le travail de création, capricieux par essence, c\'est vrai qu\'il est impossible de commander à l\'esprit si on ne vit pas sa création. Et la vie même est loin d\'être quelque chose d\'ordonné, \"un long fleuve tranquille\" ;-)', NULL),
(6, 629, 260, 1487506524, 4, 5, NULL, NULL),
(7, 633, 260, 1489578009, 4, 4, 'Effectivement je retrouve avec plaisir ce synopsis d\'un film que j\'ai vu plusieurs fois. J\'ai peut-être juste une objection sur la désinence. Pourquoi un monde de mâle ? Des femmes flics auraient-elles agi différemment ? Elles sont devenues l\'ennemi public numéro un, c\'est une course poursuite qu\'elles ont elles mêmes déclenchée, se comportant aussi efficacement que des hommes contre le \"système\". Et dès le braquage du flic elles montrent qu\'elles sont devenues incontrôlables, et que rien ne les ', NULL);
